import { useState, useEffect } from "react";

import "../../styles/root/RandomQuoteGenerator.scss";

export const RandomQuoteGenerator = () => {
  const [quote, setQuote] = useState<string>("");
  const [author, setAuthor] = useState<string>("");
  const [randomNumber, setRandomNumber] = useState<number>(1);
  const [image, setImage] = useState<string>("");

  useEffect(() => {
    fetch(`http://localhost:8000/api/quotes`, {
      method: 'POST', // Changing to POST
      headers: {
        'Content-Type': 'application/json',
        // Removed Cookie header modification attempt
      },
      body: JSON.stringify({ id: randomNumber }), // Sending data in the body
    })
    .then(response => {
      if (!response.ok) {
        throw new Error("Network response was not ok");
      }
      return response.json();
    })
    .then(data => {
      if (data.content && data.author && data.image) {
        setQuote(data.content);
        setAuthor(data.author);
        setImage(`http://localhost:8000/images/${data.image}`);
      }
    })
    .catch(error => console.error("Fetch error:", error.message));
  }, [randomNumber]);

  const handleClick = () => {
    setRandomNumber(Math.floor(Math.random() * 10 + 1));
  };

  return (
    <div className="quote-generator-wrapper">
      <p className="quote-text">{quote}</p>
      <div className="quote-nav-bars">
        <div className="quote-author">
          <img src={image} alt={author} />
          <h1>{author}</h1>
        </div>
        <button className="quote-button" onClick={handleClick}>
          Get another button
        </button>
      </div>
    </div>
  );
};
