import { useState, useEffect } from "react";

import "../../styles/root/RandomQuoteGenerator.scss";

export const RandomQuoteGenerator = () => {
  const [quote, setQuote] = useState<string>("");
  const [author, setAuthor] = useState<string>("");
  const [randomNumber, setRandomNumber] = useState<number>(1);
  const [image, setImage] = useState<string>("");

  useEffect(() => {
    fetch(`http://localhost:8000/api/quotes/${randomNumber}`)
      .then((response) => {
        if (!response.ok) {
          throw new Error("Network response was not ok");
        }
        return response.json();
      })
      .then((data) => {
        if (data.content && data.author && data.image) {
          setQuote(data.content);
          setAuthor(data.author);

          fetch(data.image)
            .then((response) => response.blob())
            .then((blob) => {
              // Alternative approach using URL.createObjectURL
              const imageUrl = URL.createObjectURL(blob);
              setImage(imageUrl);

              // Assuming `imageUrl` is the URL of the image you want to convert to Base64
              fetch(imageUrl)
                .then((response) => response.blob()) // Convert the response to a blob
                .then((blob) => {
                  const reader = new FileReader();
                  reader.onloadend = () => {
                    const base64data = reader.result;
                    console.log(base64data); // Use this Base64 string as needed
                    setImage(base64data); // Directly set the Base64 string as the image source
                  };
                  reader.onerror = (error) => console.error("Error reading blob:", error);
                  reader.readAsDataURL(blob); // Read the blob as a data URL (Base64)
                })
                .catch((error) =>
                  console.error("Error fetching the image:", error)
                );
            });
        }
      })
      .catch((error) => console.error("Fetch error:", error.message));
  }, [randomNumber]);

  const handleClick = () => {
    setRandomNumber(Math.floor(Math.random() * 10 + 1));
  };

  return (
    <div className="quote-generator-wrapper">
      <p className="quote-text">{quote}</p>
      <div className="quote-nav-bars">
        <div className="quote-author">
          <img src={image} alt={author} />
          <h1>{author}</h1>
        </div>
        <button className="quote-button" onClick={handleClick}>
          Get another button
        </button>
      </div>
    </div>
  );
};
